INSERT INTO html VALUES (1,'mailinglist','Mailing List','<h3>\nMailing List Instructions\n</h3>\nTo join the Bay Area Vanguard 15 mailing list go to <A HREF=\"http://myfleet.org/mailman/listinfo/sfv15\"> this page</A>.  If you have trouble getting to this page, send me mail at <A HREF=\"mailto: dbyron@dbyron.com\">dbyron@dbyron.com</A>.\n<br>\n<br>\n<p>\nThe list address is\n<A HREF=\"mailto: sfv15@myfleet.org\">sfv15@myfleet.org</A>. So save this address and send all you future communications to this address AFTER you subscribe. I set it up as an open list, meaning anyone can subscribe.  But, I restricted it so that only members of the list can send messages to the list. This will keep junk mail out. So please subscribe right now!',20060910095345);
INSERT INTO html VALUES (2,'links','Links','<h2>Links</h2>\n\n<b>Vanguard 15 Class </b><BR>\n<a href=\"http://www.v15.org\">National class association</a><BR>\n<a href=\"http://www.v15socal.org\">Southern California, Fleet 61</a><BR>\n<a href=\"http://www.willamettesailingclub.com/fleets/v15/index.html\">Portland, OR, Fleet 69</a><BR>\n<a href=\"http://170.224.10.44/DesktopDefault.aspx?tabindex=10&tabid=16\">Westport, CT, Fleet 20</a><BR>\n<a href=\"http://www.spiweb.net/v15/fleet67\">Chicago, IL, Fleet 45</a><BR>\n<a href=\"http://www.severnsailing.org/fleets/v15/\">Annapolis, MD, Fleet 59</a><BR>\n\n<p>\n<b>Sailing Governing Bodies & Rules</b></a><BR>\n<a href=\"http://www.sailing.org\">International Sailing Federation (ISFA) site</a><BR>\n<a href=\"http://www.ussailing.org\">US Sailing Association (USSA) site</a><BR>\n<a href=\"http://www.collegesailing.org\">US College Sailing Association (ICSA) site</a><BR>\n<a href=\"http://www.highschoolsailingusa.org\">US High School Sailing Association (ISSA) site</a><BR>\n\n<p>\n<b>Team Racing</b><BR>\n<a href=\"http://www.ustra.org\">US Team Racing Association (USTRA) site</a><BR>\n<a href=\"http://www.teamracing.org\">UK Team Racing Association (UKTRA) site</a><BR>\n\n<p>\n<b>Equipment</b><BR>\n<a href=\"http://www.teamvanguard.com\">Vanguard Sailboats, V15 manufacturer</a><BR>\n<a href=\"http://www.svendsens.com\">Svendsens - SF Bay Vanguard 15 dealer</a><BR>\n<a href=\"http://www.westmarine.com\">West Marine</a><BR>\n<a href=\"http://www.apsltd.com\">APS (parts, dollies, clothing)</a><BR>\n<a href=\"http://www.seitech.com\">Seitech (dollies)</a><BR>\n\n<p>\n<b>Weather</b><BR>\n<a href=\"http://www.iwindsurf.com\">World-wide real-time bouy reports and professional forecasts</a><BR>\n<a href=\"http://www.wrh.noaa.gov/total_forecast/marine.php?marine=pzz530\">SF Bay marine forecast</a><BR>\n<a href=\"http://sfports.wr.usgs.gov/cgi-bin/wind/windbin.cgi\">SF Bay real-time wind patterns</a><BR>\n<a href=\"http://tbone.biol.s