INSERT INTO regatta VALUES (27,'2000-11-11','2000-11-12',20001116181520,'Fleet Camping Regatta',14,48,6,'','1,Hackworth & Hackworth,46,4,2,4,1,4,3,4,2,6,1,5,2,1,1,3,3\r\n2,Bennett & Bennett,56,2,3,1,2,2,1,3,5,3,4,2,6,5,5,6,6\r\n3,Horsch & Horsch,65,6,5,3,5,5,2,1,3,5,6,1,5,6,6,5,1\r\n4,Pohl & Carr,71,5,1,2,3,1,4,2,6,7,8,4,4,8,8,4,4\r\n5,Deeds & Dan,75,1,4,5,4,3,5,6,4,8,2,6,1,4,7,7,8\r\n6,Sellers & Sellers,104,9,9,9,9,9,9,9,9,2,3,7,8,2,4,1,5\r\n7,Adamson & Adamson,108,9,9,9,9,9,9,9,9,1,7,3,7,7,2,2,7\r\n8,Smit & Fousek,108,9,9,9,9,9,9,9,9,4,5,8,3,3,3,8,2\r\n9,Houser & Cara,119,3,6,6,6,6,6,5,1,10,10,10,10,10,10,10,10\r\n10,Palace & Leah,133,8,8,7,8,8,7,8,7,9,9,9,9,9,9,9,9\r\n11,Geer & Amy,139,7,7,8,7,7,8,7,8,10,10,10,10,10,10,10,10','<HTML><HEAD><TITLE>Fleet Camping Regatta</TITLE></HEAD>\r\n<BODY>\r\n<H2>FLEET CAMPING REGATTA</H2>\r\n<H3>\r\nNOVEMBER 11TH & 12TH<br>\r\nSAILING AT INVERNESS YACHT CLUB<br>\r\nCAMPING AT SAMUEL P. TAYLOR PARK<br>\r\nBOTH ARE ABOUT AN HOUR NORTH OF SAN FRANCISCO<P>\r\n\r\nSCHEDULE<p>\r\nSaturday<br>\r\nSkippers Meeting:  0930<BR>\r\nFirst Race:  1030<BR>\r\nLast Race of day:  1400<P>\r\n\r\nSunday<Br>\r\nFirst Race:  1030<br>\r\nLast Race:  1430<p>\r\n\r\nWe have researved 2 sites \r\nfor Friday night and 4 sites for Saturday night(8 people 2 cars per site, \r\ndogs allowed on +6\' leash).  Cars can be left at Inverness or parking areas \r\nof the Park.   We will adjust it as needed.<p>\r\n\r\nDIRECTIONS:<br>\r\n<a href=\"http://parks.ca.gov/north/marin/map233.htm\">Samuel P. Taylor State Park</a><br>\r\n<a href=\"http://www.sailtomalesbay.com\">Tomales Bay</a><p>\r\n\r\nANY QUESTIONS PLEASE EMAIL:<br>\r\n<A HREF=\"MAILTO: RLJONAS@AOL.COM\">RLJONAS@AOL.COM</A><p>\r\n\r\nBART AND BECKY HACKWORTH\r\n</H3>\r\n</BODY>\r\n</HTML>','<H2>AWESOME EVENT!!</H2>\r\nBig thanks to Bart and Becky for organizing it and to the Inverness Yacht Club for letting us use their club.<p>\r\nHere are some pic\'s (compliments of Banana, the IYC Commodore):<br>\r\n