INSERT INTO regatta VALUES (32,'2001-04-07','2001-04-08',20010209100559,'SDYC Team Race',15,3,0,'',NULL,'<H2>SAN DIEGO TEAM RACE CLASSIC REGATTA NOR</h2>\r\n\r\n<p>Come and celebrate the spirit of team racing at this year\'s Contemporary Team Race Classic Regatta at San Diego Yacht Club April 7 &amp; 8.</br>\r\n\r\n<p>There is possibly no more enjoyable place to spend an early\r\nApril weekend.</p>\r\n\r\n<p>The regatta features classic dinghy team racing in club\r\nprovided flying Junior sailboats (FJ\'s), on the water umpires, competitive waterfront and battleground, social atmosphere, incredible spectating and clubhouse facilities.<br>\r\n\r\n<p>Come join in as a competitor, umpire, race committee, or\r\nteam racing enthusiast.</p>\r\n\r\n<H3>Schedule:</H3>\r\n\r\n<p>7pm Friday 6 April - Tactics and Strategy Symposium with\r\nComputer animations.<br>\r\n\r\n<p>Get a peek at the power of sailing animations at: <A HREF=\"http://www.obyc.com/teamrace/ctr.html\">http://www.obyc.com/teamrace/ctr.html</A><br>\r\n\r\n<p>Saturday and Sunday 7,8 April - Contemporary Team Racing at\r\nSDYC.<br>\r\n\r\n<p>Saturday evening Social at Club.</p>\r\n\r\n<A HREF=\"http://www.vanguard15.org/pdf/2001SDYCTeamRaceSI.pdf\">Regatta Sailing Instructions(.pdf)</A><BR>\r\n\r\n<p>Housing available.  Cost: $20 / person includes lunches and Saturday evening social.  RSVP ASAP to reserve your team\'s berth at this regatta.<br><br>\r\n\r\nGavin OHare<br>\r\nTel:619 225 0117<br>\r\nFax:619 225 1522<br>\r\n<A HREF=\"mailto:go\'hare@nalco.com\">gohare@nalco.com</A><br>\r\n\r\n</div>\r\n</body>\r\n</html>',NULL);
